import React, { Component } from "react";
import { connect } from "react-redux";
import App from "../../App";
import ApodResult from "../components/apodResult";
import Failure from "../../components/failure";

class ApodStatus extends Component {
  render() {
    let renderedApod;

    if (!this.props.showResult) {
      renderedApod = <App />;
    }
    if (this.props.showResult) {
      renderedApod = <ApodResult receivedInfo={this.props.receivedInfo} />;
    }
    if (this.props.failure) {
      renderedApod = <Failure />;
    }
    return <div>{renderedApod}</div>;
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    showResult: state.showResult,
    receivedInfo: state.receivedInfo,
    failure: state.isFailure
  };
};

const ApodConnected = connect(mapStateToProps)(ApodStatus);
export default ApodConnected;
