import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import { nameUpdate, dateUpdate } from "../actions/formActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

const styles = theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "rgba(200, 200, 200, .5)",
    marginLeft: 400,
    marginRight: 400,
    borderRadius: "10%"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  contact: {
    position: "relative",
    bottom: 60
  },
  header: {
    fontFamily: "Righteous",
    color: "#d8dcf2",
    height: 80,
    fontSize: 50,
    marginTop: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  form: {
    width: 300,
    marginBottom: 20
  },
  button: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  }
});

class MrpForm extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <div className={classes.contact}>
          <h1 className={classes.header}>Mars Rover Photos</h1>
          <p className={classes.helper}>Please select a rover and a date</p>
          <div className={classes.root} />
          <Button
            variant="contained"
            color="primary"
            className={classes.button}
            type="submit"
            onClick={this.handleSubmit}
          >
            Submit
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    name: state.mrpForm.name,
    date: state.mrpForm.date
  };
};

MrpForm.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(MrpForm);
const mrpFormConnected = connect(mapStateToProps)(withstyles);
export default mrpFormConnected;
