import React, { Component } from "react";
import { Animated } from "react-animated-css";

const styles = theme => ({
  loadingStyle: {
    position: "relative",
    bottom: 100
  },

  container: {
    display: "grid",
    gridTemplateColumns: "auto",
    justifyContent: "center",
    position: "relative",
    bottom: "80px"
  },

  button: {
    position: "relative",
    top: 30
  }
});

class Main extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <div className="ShowCase">
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <h1>Museum Locale</h1>
          </Animated>
          <p>
            <strong>Your Local Museum</strong>
          </p>
        </div>
        <img
          className="Icons"
          src="https://pbs.twimg.com/profile_images/472484809390845952/al5G_CPq_400x400.jpeg"
          alt="TheMet"
        />
        <img
          className="Icons"
          src="http://blogs.newschool.edu/arts-social-engagement/files/2018/05/met.jpg"
          alt="TheMet"
        />
        <img
          className="Icons"
          src="https://upload.wikimedia.org/wikipedia/commons/f/fc/Artsy_logo.svg"
          alt="TheMet"
        />
        <h1 style={{ color: "#d8dcf2" }}>{this.props.title}</h1>
        <h3 style={{ color: "#d8dcf2" }}>{this.props.content}</h3>
        <img src={this.props.image} alt="" />
      </React.Fragment>
    );
  }
}

export default Main;
