import React from "react";
import "./App.css";
import { Animated } from "react-animated-css";
import SelectForm from "./container/selectForm";
import Result from "./components/results";
import { connect } from "react-redux";

const App = () => {
  //FUNCTIONALITY ==> state'e atadığın bir şeye göre, ya selectForm'u, ya da Result'ı render et.
  let pageRendered;
  if (this.props.showResults === true) {
    pageRendered = <Result />;
  } else {
    pageRendered = <SelectForm />;
  }

  return (
    <React.Fragment>
      <div className="ShowCase">
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <h1>Museum Locale</h1>
        </Animated>
        <p>
          <strong>Your Local Museum</strong>
        </p>
      </div>
      <img
        className="Icons"
        src="https://pbs.twimg.com/profile_images/472484809390845952/al5G_CPq_400x400.jpeg"
        alt="TheMet"
      />
      <img
        className="Icons"
        src="http://blogs.newschool.edu/arts-social-engagement/files/2018/05/met.jpg"
        alt="TheMet"
      />
      <img
        className="Icons"
        src="https://upload.wikimedia.org/wikipedia/commons/f/fc/Artsy_logo.svg"
        alt="TheMet"
      />
      {pageRendered}
    </React.Fragment>
  );
};

//APP component'ı state'e bağlansın; render etttiği selectForm ya da Result'a, gerekli parametreleri prop olarak göndersin.

const mapStateToProps = state => {
  return {
    showResults: state.showResults
  };
};

const AppConnected = connect(mapStateToProps)(App);
export default AppConnected;
