import React, { Component } from "react";
import ApodResult from "./components/apodResult";
import MrpStatus from "./container/mrpStatus";
import Failure from "./components/failure";
import { connect } from "react-redux";
import Main from "./components/main";

export class App extends Component {
  render() {
    let pageRendered;

    if (!this.props.submitted) {
      pageRendered = <Main />;
    }

    if (this.props.showApod) {
      pageRendered = <ApodResult receivedInfo={this.props.receivedInfo} />;
    }

    if (this.props.failure) {
      pageRendered = <Failure />;
    }

    return <div>{pageRendered}</div>;
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    submitted: state.isSubmitted,
    showApod: state.showApod,
    mrpSubmitted: state.isMrpSubmitted,
    showMrp: state.showMrp,
    failure: state.isFailure,
    receivedInfo: state.receivedInfo
  };
};

const AppConnected = connect(mapStateToProps)(App);
export default AppConnected;
