import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import IvlList from "./ivlList";

const styles = {
  header: {
    color: "#d8dcf2",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 70
  },
  all: {
    backgroundColor: "#212127d3",
    borderRadius: "5%",

    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    backgroundColor: "#e040fb",
    position: "relative",
    bottom: 40,
    width: 300,
    height: 50
  },
  date: {
    color: "#E040FB"
  }
};

class ivlResult extends Component {
  render() {
    const { classes } = this.props;

    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>

        <div className={classes.all}>
          <div className={classes.body}>
            <h1 className={classes.header}>
              {this.props.keyword.toUpperCase()}
            </h1>
            <h1 className={classes.info}>{this.props.year}</h1>
            {this.props.receivedInfo.info.map(i => (
              <IvlList
                key={i.data.map(e => e.nasa_id)}
                title={i.data.map(e => e.title)}
                description={i.data.map(e => e.description)}
                photo={i.links.map(e => e.href)}
              />
            ))}
            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              style={{ marginBottom: 70 }}
              href="/ivl"
            >
              Go Back
            </Button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    keyword: state.ivlForm.keyword,
    year: state.ivlForm.year
  };
};

ivlResult.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(ivlResult);
const ivlResultConnected = connect(mapStateToProps)(withstyles);
export default ivlResultConnected;
