import React, { Component } from "react";
import SelectForm from "./container/selectForm";
import formResult from "./components/formResult";
import { connect } from "react-redux";
import Main from "./components/main";

export class App extends Component {
  render() {
    let pageRendered;

    if (!this.props.submitted) {
      pageRendered = (
        <div>
          <Main /> <SelectForm />
        </div>
      );
    }

    if (this.props.showResults) {
      pageRendered = (
        <div>
          <Main /> <formResult />
        </div>
      );
    }
    if (this.props.loading) {
      pageRendered = (
        <Main
          title={this.props.loadingContent.title}
          content={this.props.loadingContent.content}
          image="https://media.giphy.com/media/LbBSU26sSRAE8/giphy.gif"
        />
      );
    }
    if (this.props.failure) {
      pageRendered = (
        <Main
          title={this.props.failureContent.title}
          content={this.props.failureContent.content}
        />
      );
    }

    return <div>{pageRendered}</div>;
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    submitted: state.submitted,
    showResults: state.form.showResults,
    loading: state.isLoading,
    loadingContent: state.loadingContent,
    failure: state.isFailure,
    failureContent: state.failureContent
  };
};

const AppConnected = connect(mapStateToProps)(App);
export default AppConnected;
