import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const style = {
  header: {
    color: "#ffcc00",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "40px",
    position: "relative",
    bottom: "100px"
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat( auto-fill, minmax(420px, 1fr) )",
    gridColumnGap: "10px",
    gridRowGap: "70px",
    position: "relative",
    bottom: "80px"
  },
  text: {
    color: "30323e",
    fontFamily: "Quicksand",
    backgroundColor: "#d8dcf2",
    opacity: "0.6",
    fontWeight: "bold"
  }
};

function FormResult(props) {
  const { classes } = props;

  return (
    <React.Fragment>
      <Toolbar>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <Button className={classes.home} href="/">
            Universa Localis
          </Button>
        </Animated>
      </Toolbar>
      <div>
        <div className={classes.header}>
          <h1>{props.receivedInfo.title}</h1>
          <h3>{props.receivedInfo.date}</h3>
          <h3>by {props.receivedInfo.artist}</h3>
        </div>
        <div style={gridStyle}>
          <div style={textStyle}>
            <h3>About the Work</h3>
            <p>{props.receivedInfo.explanation}</p>
          </div>
          <iframe
            title="video"
            width="420"
            height="315"
            src={props.receivedInfo.link}
          >
            {" "}
          </iframe>
        </div>
        <Button
          variant="contained"
          color="primary"
          style={{ marginBottom: 70 }}
          href="/"
        >
          Go Back
        </Button>
      </div>
    </React.Fragment>
  );
}

export default withStyles(styles)(FormResult);
