import {
  ARTIST_UPDATE,
  MOVEMENT_UPDATE,
  CENTURY_UPDATE,
  BRANCH_UPDATE,
  SUBMITTED,
  LOADING,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  RESET
} from "../Actions/ActionTypes";

const initialState = {
  form: {
    artist: "",
    movement: "",
    century: "",
    branch: "",
  },
  isSubmitted: false,
  isLoading: false,
  loadingContent: {
    title: "Loading...",
    content: "Please Wait!"
  },
  isSuccess: false,
  isFailure: false,
  reset: false
};

function reducer(state = initialState, action) {
  switch (action.type) {
    case ARTIST_UPDATE: {
      const newState = Object.assign({}, state);
      newState.form.artist = action.payload;
      return newState;
    }
    case MOVEMENT_UPDATE: {
      const newState = Object.assign({}, state);
      newState.form.movement = action.payload;
      return newState;
    }
    case CENTURY_UPDATE: {
      const newState = Object.assign({}, state);
      newState.form.century = action.payload;
      return newState;
    }
    case BRANCH_UPDATE: {
      const newState = Object.assign({}, state);
      newState.form.branch = action.payload;
      return newState;
    }
    case SUBMITTED:
      return Object.assign({}, state, {
        submitted: action.payload
      });
    case LOADING:
      return Object.assign({}, state, {
        isLoading: action.payload
      });
    case FETCH_SUCCESS:
      return Object.assign({}, state, {
        isSuccess: action.payload
      });
    case FETCH_FAILURE:
      return Object.assign({}, state, {
        isFailure: action.payload
      });
    case RESET: {
      const newState = Object.assign({}, (state = initialState));
      newState.form.artist = action.payload;
      newState.form.movement = action.payload;
      newState.form.century = action.payload;
      newState.form.branch = action.payload;
      return JSON.parse(JSON.stringify(newState));
    }
    default:
      return state;
  }
}

export default reducer;
