import {
  LOADING,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  SHOW_RESULTS,
  RESET
} from "../actions/formActions/action_types";

const initialState = {
  submitted: false,
  isLoading: false,
  loadingContent: {
    title: "Loading...",
    content: "Please Wait!"
  },
  isSuccess: false,
  isFailure: false,
  failureContent: {
    title: "Sorry!",
    content: "There has been a problem!"
  },
  showResults: false,
  receivedInfo: {},
  reset: false,
  dialog_open: false
};

function FormReducer(state = initialState, action) {
  switch (action.type) {
    case LOADING:
      return Object.assign({}, state, {
        isLoading: action.payload
      });
    case FETCH_SUCCESS:
      return Object.assign({}, state, {
        isSuccess: action.payload
      });
    case FETCH_FAILURE:
      return Object.assign({}, state, {
        isFailure: action.payload
      });
    case SHOW_RESULTS:
      return Object.assign({}, state, {
        showResults: action.payload,
        receivedInfo: action.receivedObject
      });
    case RESET: {
      const newState = Object.assign({}, (state = initialState));
      newState.form.artist = action.payload;
      newState.form.movement = action.payload;
      newState.form.century = action.payload;
      newState.form.branch = action.payload;
      return JSON.parse(JSON.stringify(newState));
    }
    default:
      return state;
  }
}

export default FormReducer;
