import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#d8dcf2",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    //display: "grid",
    //gridTemplateColumns: "repeat(auto-fill, minmax(100px, 1000px))",
    //gridAutoRows: "minmax(10px, auto)",
    //gridRowGap: "50px",
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "80px",
    margin: "1rem"
  },
  text: {
    color: "#30323e",
    fontFamily: "Quicksand",
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    fontWeight: "bold",
    padding: 30,
    marginTop: 50
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#923600"
  },
  body: {
    position: "relative",
    top: 100
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "100px"
  },
  picture: {
    width: "100%",
    height: "auto",
    maxHeight: 700
  }
};

function FormResult(props) {
  const { classes } = props;

  return (
    <React.Fragment>
      <Toolbar>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <Button className={classes.home} href="/">
            Universa Localis
          </Button>
        </Animated>
      </Toolbar>
      <div className={classes.body}>
        <div>
          <h1 className={classes.header}>{props.receivedInfo.title}</h1>
          <h3 className={classes.info}>Date: {props.receivedInfo.date}</h3>
          <h3 className={classes.info}>by {props.receivedInfo.artist}</h3>
        </div>
        <div className={classes.grid}>
          <img
            className={classes.picture}
            src={props.receivedInfo.link}
            alt=""
          />
          <div className={classes.text}>
            <h3>About the Work</h3>
            <p>{props.receivedInfo.explanation}</p>
          </div>
        </div>
        <Button
          variant="contained"
          color="primary"
          style={{ marginBottom: 70 }}
          href="/"
        >
          Go Back
        </Button>
      </div>
    </React.Fragment>
  );
}

export default withStyles(styles)(FormResult);
