import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import { connect } from "react-redux";
import { getPicture } from "../actions/formActions/actions";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";

const styles = theme => ({
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
    width: 250,
    WebkitTextFillColor: "#d8dcf2"
  },

  container: {
    display: "grid",
    gridTemplateColumns: "auto",
    justifyContent: "center",
    position: "relative",
    bottom: "170px"
  },

  button: {
    position: "relative",
    top: 30
  }
});

class SelectForm extends React.Component {
  render() {
    const { classes } = this.props;
    return (
      <div>
        <div className={classes.container}>
          <Button
            variant="contained"
            color="secondary"
            className={classes.button}
            type="submit"
            onClick={() => this.props.dispatch(getPicture())}
          >
            Submit
          </Button>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    artist: state.form.artist,
    movement: state.form.movement,
    century: state.form.century,
    branch: state.form.branch,
    dialog_open: state.dialog_open,
    astro_picture: state.astro_picture
  };
};

SelectForm.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(SelectForm);
const selectFormConnected = connect(mapStateToProps)(withstyles);
export default selectFormConnected;
