import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb"
  },
  date: {
    color: "#E040FB"
  }
};

function MrpResult(props) {
  const { classes } = props;

  const names = [];
  const earth_dates = [];
  const cameras = [];
  const img_srcs = [];

  names.push(props.receivedInfo.name);
  earth_dates.push(props.receivedInfo.earth_date);
  cameras.push(props.receivedInfo.camera);
  img_srcs.push(props.receivedInfo.img_src);

  let nameShow;
  for (let i = 0; i < names.length; i++) {
    nameShow = names[i];
  }

  let dateShow;
  for (let i = 0; i < earth_dates.length; i++) {
    dateShow = <h1 className={classes.header}>{earth_dates[i]}</h1>;
  }

  let cameraShow;
  for (let i = 0; i < cameras.length; i++) {
    cameraShow = <h1 className={classes.header}>{cameras[i]}</h1>;
  }

  let imgShow;
  for (let i = 0; i < img_srcs.length; i++) {
    imgShow = <h1 className={classes.header}>{img_srcs[i]}</h1>;
  }

  return (
    <React.Fragment>
      <Toolbar>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <Button className={classes.home} href="/">
            Universa Localis
          </Button>
        </Animated>
      </Toolbar>
      <div className={classes.all}>
        <div className={classes.body}>
          <div>
            <h1 className={classes.header}>{nameShow}</h1>
            <h1 className={classes.header}>{dateShow}</h1>
            <h1 className={classes.header}>{cameraShow}</h1>;
          </div>
          <div className={classes.grid}>{imgShow}</div>
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            style={{ marginBottom: 70 }}
            href="/mrp"
          >
            Go Back
          </Button>
        </div>
      </div>
    </React.Fragment>
  );
}

export default withStyles(styles)(MrpResult);
