import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { artistUpdate, onClose, onOpen } from './Actions/formActions/actions'

const styles = theme => ({
  button: {
    display: 'block',
    marginTop: theme.spacing.unit * 2,
  },
  form: {
    width: '30%',
    display: 'flex',
    flexDirection: 'column'
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  },
});

class SelectForm extends React.Component {
  render() {
    const { classes } = this.props;

    return (
      <form className={classes.form}>
        <FormControl className={classes.formControl}>
          <InputLabel>Artist</InputLabel>
          <Select
            value={this.props.artist}
            open={this.props.open}
            onClose={() => {this.props.dispatch(onClose())}}
            onOpen={() => {this.props.dispatch(onOpen())}}
            onChange={(event) => {
              let value = event.target.value;
              this.props.dispatch(artistUpdate(value))
            }}>
          <MenuItem value='Alican'>Alican</MenuItem>
          <MenuItem value='Tansu'>Tansu</MenuItem>
          <MenuItem value='Tolga'>Tolga</MenuItem>
          </Select>
        </FormControl>

        <FormControl className={classes.formControl}>
          <InputLabel>Movement</InputLabel>
          <Select
            value={this.props.artist}
            open={this.props.open}
            onClose={() => {this.props.dispatch(onClose())}}
            onOpen={() => {this.props.dispatch(onOpen())}}
            onChange={(event) => {
              let value = event.target.value;
              this.props.dispatch(artistUpdate(value))
            }}>
          <MenuItem value='Surrealism'>Surrealism</MenuItem>
          <MenuItem value='Minimalism'>Minimalism</MenuItem>
          <MenuItem value='Impressionism'>Impressionism</MenuItem>
          </Select>
        </FormControl>
      </form>
    );
  }
}


const mapStateToProps = state => {
  return {
    artist: state.form.artist,
    movement: state.form.movement,
    century: state.form.century,
    branch: state.form.branch,
    dialog_open: state.form.dialog_open
  };
};

SelectForm.propTypes = {
  classes: PropTypes.object.isRequired,
};

const withstyles = withStyles(styles)(SelectForm);
const selectFormConnected = connect(mapStateToProps)(withstyles);
export default selectFormConnected;
