import React, { Component } from "react";
import "./TextFields/Main.css";
import Autosuggest from "react-autosuggest";
import Button from "@material-ui/core/Button";
import { artists } from "./TextFields/Artists";
import Movements from "./TextFields/Movements";
import Centuries from "./TextFields/Centuries";
import Branches from "./TextFields/Branches";
import {
  artistUpdate,
  movementUpdate,
  centuryUpdate,
  branchUpdate,
  fetchStart
} from "../Actions/Actions";

const getSuggestion = value => {
  const inputValue = value.trim().toLowerCase();
  const inputLength = inputValue.length;

  return inputLength === 0
    ? []
    : getState().form.filter(
        lang => lang.name.toLowerCase().slice(0, inputLength) === inputValue
      );
};

class Main extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    let value = event.target.value;
    if (event.target.name === "artist") {
      this.props.dispatch(artistUpdate(value));
    }
    if (event.target.name === "movement") {
      this.props.dispatch(movementUpdate(value));
    }
    if (event.target.name === "century") {
      this.props.dispatch(centuryUpdate(value));
    }
    if (event.target.name === "branch") {
      this.props.dispatch(branchUpdate(value));
    }
  }

  render() {
    return (
      <form onSubmit={() => this.props.dispatch(fetchStart())}>
        <Autosuggest
          suggestions={suggestions}
          onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
          onSuggestionsClearRequested={this.onSuggestionsClearRequested}
          getSuggestionValue={getSuggestionValue}
          renderSuggestion={renderSuggestion}
          inputProps={inputProps}
          value={this.props.artist}
          onChange={this.handleChange}
        />
        <Button
          variant="contained"
          color="secondary"
          style={{ position: "relative", bottom: 50 }}
        >
          Submit
        </Button>
      </form>
    );
  }
}

export default Main;
