import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import {
  nameUpdate,
  emailUpdate,
  messageUpdate,
  validateForm
} from "../../actions/contactActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import FormHelperText from "@material-ui/core/FormHelperText";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import BottomNavigation from "@material-ui/core/BottomNavigation";
import BottomNavigationAction from "@material-ui/core/BottomNavigationAction";

const styles = theme => ({
  root: {
    display: "grid",
    alignItems: "center",
    backgroundColor: "rgba(200, 200, 200, .5)",
    borderRadius: 20,
    maxWidth: "40%"
  },
  contact: {
    position: "relative",
    bottom: 80
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb",
    float: "left"
  },
  buttonContainer: {
    width: "95%",
    display: "block"
  },
  button: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  },
  header: {
    marginTop: "1em",
    fontFamily: "Righteous",
    color: "#d8dcf2",
    fontSize: "5em"
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand"
  },
  form: {
    width: 300,
    marginBottom: 20
  },
  button1: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  }
});

class Contact extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    let value = event.target.value;
    if (event.target.name === "name") {
      this.props.dispatch(nameUpdate(value));
    }
    if (event.target.name === "email") {
      this.props.dispatch(emailUpdate(value));
    }
    if (event.target.name === "message") {
      this.props.dispatch(messageUpdate(value));
    }
  }

  handleSubmit() {
    this.props.dispatch(validateForm());
  }

  render() {
    const { classes } = this.props;
    let showNameError;
    let showEmailError;
    let showMessageError;

    if (this.props.errorFound) {
      this.props.errorArray.forEach(error => {
        if (error === 1) {
          showNameError = "Name field can't be empty!";
        } else if (error === 2) {
          showEmailError = "Email address is not valid!";
        } else if (error === 3) {
          showEmailError = "Email field can't be empty! ";
        } else if (error === 4) {
          showMessageError = "Message field can't be empty!";
        }
      });
    }

    return (
      <React.Fragment>
        <div id="container">
          <Toolbar className={classes.buttonContainer}>
            <Animated
              animationIn="fadeInRight"
              animationOut="fadeOut"
              isVisible={true}
            >
              <Button id="home" className={classes.home} href="/">
                Universa Localis
              </Button>
            </Animated>
            <div id="bb">
              <Button id="buttons" className={classes.buttons} href="/">
                Home
              </Button>
              <Button id="buttons" className={classes.buttons} href="about">
                About
              </Button>
              <Button id="buttons" className={classes.buttons} href="contact">
                Contact
              </Button>
            </div>
          </Toolbar>

          <div className={classes.contact}>
            <h1 className={classes.header}>Contact</h1>
            <p className={classes.helper}>We'd like to hear from you!</p>
            <div className={classes.root}>
              <FormControl required className={classes.form}>
                <TextField
                  required
                  name="name"
                  label="Name"
                  value={this.props.name}
                  onChange={this.handleChange}
                />
                <FormHelperText error>{showNameError}</FormHelperText>
              </FormControl>
              <FormControl required className={classes.form}>
                <TextField
                  required
                  className={classes.field}
                  name="email"
                  label="Email"
                  value={this.props.email}
                  onChange={this.handleChange}
                />
                <FormHelperText error>{showEmailError}</FormHelperText>
              </FormControl>
              <FormControl required className={classes.form}>
                <TextField
                  required
                  name="message"
                  label="Message"
                  multiline="true"
                  rows={5}
                  value={this.props.message}
                  onChange={this.handleChange}
                />
                <FormHelperText error>{showMessageError}</FormHelperText>
              </FormControl>
            </div>
            <Button
              variant="contained"
              color="primary"
              className={classes.button1}
              type="submit"
              onClick={this.handleSubmit}
            >
              Submit
            </Button>
          </div>
        </div>
        <BottomNavigation showLabels id="footer">
          <BottomNavigationAction id="link" label="Home" href="/" />
          <BottomNavigationAction id="link" label="About" href="about" />
          <BottomNavigationAction id="link" label="Contact" href="contact" />
          <p id="copyright">© copyright Mehmet Tansu Işıklı</p>
        </BottomNavigation>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.ContactReducer;
  return {
    name: state.form.name,
    email: state.form.email,
    message: state.form.message,
    errorArray: state.errorArray
  };
};

Contact.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(Contact);
const contactConnected = connect(mapStateToProps)(withstyles);
export default contactConnected;
