import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import { mrpNameUpdate, mrpDateUpdate } from "../actions/formActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Button from "@material-ui/core/Button";

const styles = theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    flexWrap: "wrap",
    alignItems: "center",
    backgroundColor: "rgba(200, 200, 200, .8)",
    backgroundPosition: "absolute",
    marginLeft: 400,
    marginRight: 400,
    borderRadius: "10%",
    padding: 30
  },
  formControl: {
    margin: theme.spacing.unit,
    width: 300,
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing.unit * 2
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  header: {
    fontFamily: "Righteous",
    color: "#E040FB",
    height: 80,
    fontSize: "3rem",
    marginTop: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  form: {
    width: 300,
    marginBottom: 20
  },
  button: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  }
});

class MrpForm extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    //this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    let value = event.target.value;
    if (event.target.name === "name") {
      this.props.dispatch(mrpNameUpdate(value));
    }
    if (event.target.name === "date") {
      this.props.dispatch(mrpDateUpdate(value));
    }
  }

  //handleSubmit() {
  //this.props.dispatch(validateForm());
  //}

  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <h1 className={classes.header}>Mars Rover Photos</h1>
        <p className={classes.helper}>Please select a rover and a date</p>
        <div className={classes.root}>
          <FormControl variant="outlined" className={classes.formControl}>
            <InputLabel
              ref={ref => {
                this.InputLabelRef = ref;
              }}
              htmlFor="outlined-age-native-simple"
            >
              Rover Name
            </InputLabel>
            <Select
              native
              name="name"
              value={this.props.name}
              input={
                <OutlinedInput name="age" id="outlined-age-native-simple" />
              }
            >
              <option value="" />
              <option value={10}>Ten</option>
              <option value={20}>Twenty</option>
              <option value={30}>Thirty</option>
            </Select>
          </FormControl>

          <FormControl variant="outlined" className={classes.formControl}>
            <InputLabel
              ref={ref => {
                this.InputLabelRef = ref;
              }}
              htmlFor="outlined-age-native-simple"
            >
              Date
            </InputLabel>
            <Select
              native
              name="date"
              value={this.props.date}
              input={
                <OutlinedInput name="age" id="outlined-age-native-simple" />
              }
            >
              <option value="" />
              <option value={10}>Ten</option>
              <option value={20}>Twenty</option>
              <option value={30}>Thirty</option>
            </Select>
          </FormControl>

          <Button
            variant="contained"
            color="primary"
            className={classes.button}
            type="submit"
            //onClick={this.handleSubmit}
          >
            Submit
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    name: state.mrpForm.name,
    date: state.mrpForm.date
  };
};

MrpForm.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(MrpForm);
const mrpFormConnected = connect(mapStateToProps)(withstyles);
export default mrpFormConnected;
