import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';

const styles = theme => ({
  button: {
    display: 'block',
    marginTop: theme.spacing.unit * 2,
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  },
});

class SelectForm extends React.Component {

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleOpen = () => {
    this.setState({ open: true });
  };

  render() {
    const { classes } = this.props;

    return (
      <form autoComplete="off">

        <Button className={classes.button} onClick={this.handleOpen}>
          Open the select
        </Button>

        <FormControl className={classes.formControl}>
          <InputLabel htmlFor="demo-controlled-open-select">Artist</InputLabel>
          <Select
            value={this.props.artist}



            open={this.props.open}
            onClose={this.handleClose}
            onOpen={this.handleOpen}

            onChange={this.handleChange}
            inputProps={{
              name: 'age',
              id: 'demo-controlled-open-select',
            }}
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
      </form>
    );
  }
}


const mapStateToProps = state => {
  return {
    artist: state.form.artist,
    movement: state.form.movement,
    century: state.form.century,
    branch: state.form.branch,
    open: state.form.open
  };
};

SelectForm.propTypes = {
  classes: PropTypes.object.isRequired,
};

const withstyles = withStyles(styles)(SelectForm);
const selectFormConnected = connect(mapStateToProps)(withstyles);
export default selectFormConnected;
