import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import { ivlKeyUpdate, ivlYearUpdate } from "../../actions/formActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { onOpen, onClose, getIvl } from "../../actions/formActions/actions";

const styles = theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    flexWrap: "wrap",
    alignItems: "center",
    backgroundColor: "rgba(200, 200, 200, .8)",
    marginLeft: 400,
    marginRight: 400,
    borderRadius: "10%",
    padding: 30
  },
  formControl: {
    margin: theme.spacing.unit,
    width: 300,
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing.unit * 2
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  header: {
    fontFamily: "Righteous",
    color: "#E040FB",
    height: 80,
    fontSize: "3rem",
    marginTop: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  form: {
    width: 300,
    marginBottom: 20
  },
  button: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  }
});

class IvlForm extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <h1 className={classes.header}>NASA Image and Video Library</h1>
        <p className={classes.helper}>Please enter a keyword and a year</p>

        <div className={classes.root}>
          <FormControl className={classes.formControl}>
            <InputLabel>Select Rover</InputLabel>
            <Select
              value={this.props.name}
              onClose={() => {
                this.props.dispatch(onClose());
              }}
              onOpen={() => {
                this.props.dispatch(onOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(ivlKeyUpdate(value));
              }}
            >
              <MenuItem value="curiosity">Curiosity</MenuItem>
              <MenuItem value="opportunity">Opportunity</MenuItem>
              <MenuItem value="spirit">Spirit</MenuItem>
            </Select>
          </FormControl>

          <FormControl className={classes.formControl}>
            <TextField
              label="Select Date"
              type="date"
              defaultValue="2018-01-01"
              InputLabelProps={{
                shrink: true
              }}
              value={this.props.date}
              onClose={() => {
                this.props.dispatch(onClose());
              }}
              onOpen={() => {
                this.props.dispatch(onOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(ivlYearUpdate(value));
              }}
            />
          </FormControl>
          <Button
            variant="contained"
            color="primary"
            className={classes.button}
            type="submit"
            onClick={() => {
              this.props.dispatch(getIvl());
            }}
          >
            Submit
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    name: state.ivlForm.key,
    date: state.ivlForm.year,
    dialog_open: state.dialog_open
  };
};

IvlForm.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(IvlForm);
const mrpFormConnected = connect(mapStateToProps)(withstyles);
export default mrpFormConnected;
