import React from "react";
import { connect } from "react-redux";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import "./main.css";
import { getPicture } from "../actions/formActions/actions";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";

const styles = {
  card: {
    maxWidth: 345
  },
  media: {
    objectFit: "cover"
  }
};

function Main(props) {
  const { classes } = props;
  return (
    <React.Fragment>
      <div className="root">
        <Toolbar>
          <Button id="buttons" href="about">
            About
          </Button>
          <Button id="buttons" href="contact">
            Contact
          </Button>
        </Toolbar>
      </div>

      <div className="showCase">
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <h1>Universa Localis</h1>
        </Animated>
        <p>
          <strong>The whole universe on your secreen</strong>
        </p>
      </div>
      <img
        className="icons"
        src="https://upload.wikimedia.org/wikipedia/commons/thumb/e/e5/NASA_logo.svg/2000px-NASA_logo.svg.png"
        alt="Nasa"
      />

      <Card className={classes.card}>
        <CardActionArea>
          <CardMedia
            component="img"
            alt="Contemplative Reptile"
            className={classes.media}
            height="140"
            image="https://apod.nasa.gov/apod/image/1812/GeminidAurora_Hansen1_960.jpg"
            title="Contemplative Reptile"
            onClick={() => this.props.dispatch(getPicture())}
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              APOD
            </Typography>
            <Typography component="p">
              Get the astronomy picture of the day, everyday!
            </Typography>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button size="small" color="primary">
            Share
          </Button>
          <Button size="small" color="primary">
            Learn More
          </Button>
        </CardActions>
      </Card>
    </React.Fragment>
  );
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    astro_picture: state.astro_picture
  };
};

Main.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(Main);
const selectFormConnected = connect(mapStateToProps)(withstyles);
export default selectFormConnected;
