import validator from "validator";
import {
  NAME_UPDATE,
  EMAIL_UPDATE,
  MESSAGE_UPDATE,
  SUBMITTED,
  LOADING,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  RESET
} from "./action_types";

export function nameUpdate(name) {
  return function(dispatch) {
    dispatch({
      type: NAME_UPDATE,
      payload: name
    });
  };
}

export function emailUpdate(email) {
  return function(dispatch) {
    dispatch({
      type: EMAIL_UPDATE,
      payload: email
    });
  };
}

export function messageUpdate(message) {
  return function(dispatch) {
    dispatch({
      type: MESSAGE_UPDATE,
      payload: message
    });
  };
}

export function submitted() {
  return function(dispatch) {
    dispatch({
      type: SUBMITTED,
      payload: true
    });
  };
}

export function validateForm() {
  return function(dispatch, getState) {
    const possibleErrors = [];
    if (validator.isEmpty(getState().ContactReducer.form.name)) {
      possibleErrors.push(1);
    }
    if (!validator.isEmail(getState().ContactReducer.form.email)) {
      possibleErrors.push(2);
    }
    if (validator.isEmpty(getState().ContactReducer.form.email)) {
      possibleErrors.push(3);
    }
    if (validator.isEmpty(getState().ContactReducer.form.message)) {
      possibleErrors.push(4);
    }
    possibleErrors.length > 0
      ? dispatch(onError(possibleErrors))
      : dispatch(fetchStart());
  };
}

export function fetchStart() {
  return function(dispatch, getState) {
    const data = {
      name: getState().form.name,
      email: getState().form.email,
      message: getState().form.message
    };
    dispatch(loading());
    fetch("http://localhost:3000/contact", {
      method: "POST",
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(res => {
        if (res.ok) {
          dispatch(fetchSuccess());
          console.log(res.body);
        } else {
          dispatch(fetchFailure());
          console.log("failure");
        }
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

export function loading() {
  return function(dispatch) {
    dispatch({
      type: LOADING,
      payload: true
    });
  };
}

export function fetchSuccess() {
  return function(dispatch) {
    dispatch({
      type: FETCH_SUCCESS,
      payload: true
    });
  };
}

export function fetchFailure() {
  return function(dispatch) {
    dispatch({
      type: FETCH_FAILURE,
      payload: true
    });
  };
}

export function reset() {
  return function(dispatch) {
    dispatch({
      type: RESET,
      payload: ""
    });
  };
}
