import {
  SUBMITTED,
  LOADING,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  SHOW_RESULTS
} from "./action_types";

export function getApod(dispatch) {
  dispatch(loading());
  return function(dispatch) {
    fetch("http://localhost:3000/apod")
      .then(response => response.json())
      .then(data => {
        const received = {
          artist: data.copyright,
          title: data.title,
          date: data.date,
          explanation: data.explanation,
          link: data.url
        };
        dispatch(showResults(received));
      });
  };
}

export function getMrp() {
  return function(dispatch) {
    fetch("http://localhost:3000/mrp")
      .then(response => response.json())
      .then(data => {
        const received = {
          name: data.photos[0].rover.name,
          landing_date: data.photos[0].rover.landing_date,
          camera: data.photos[0].camera.full_name,
          link: data.photos[0].img_src
        };
        dispatch(showResults(received));
      });
  };
}

export function submitted() {
  return function(dispatch) {
    dispatch({
      type: SUBMITTED,
      payload: true
    });
  };
}

export function showResults(received) {
  return function(dispatch) {
    dispatch({
      type: SHOW_RESULTS,
      payload: true,
      receivedObject: received
    });
  };
}

export function loading() {
  return function(dispatch) {
    dispatch({
      type: LOADING,
      payload: true
    });
  };
}

export function fetchSuccess() {
  return function(dispatch) {
    dispatch({
      type: FETCH_SUCCESS,
      payload: true
    });
  };
}

export function fetchFailure() {
  return function(dispatch) {
    dispatch({
      type: FETCH_FAILURE,
      payload: true
    });
  };
}
