import React, { Component } from "react";
import { connect } from "react-redux";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import "./main.css";
import { getApod, getMrp } from "../actions/formActions/actions";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";

const styles = {
  body: {
    display: "grid",
    gridTemplateColumns: "1fr 1fr 1fr",
    gridColumnGap: 50,
    justifyItems: "center",
    position: "relative",
    bottom: 100,
    opacity: "0.8"
  },
  card: {
    maxWidth: 300,
    maxHeight: 250
  },
  media: {
    objectFit: "cover"
  },
  text: {
    fontFamily: "Quicksand",
    fontWeight: "bold"
  },
  failure: {
    color: "white"
  }
};

class Main extends Component {
  constructor(props) {
    super(props);

    this.handleApod = this.handleApod.bind(this);
    this.handleMrp = this.handleMrp.bind(this);
  }

  handleApod() {
    this.props.dispatch(getApod());
  }

  handleMrp() {
    this.props.dispatch(getMrp());
  }

  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <div className="root">
          <Toolbar>
            <Button id="buttons" href="about">
              About
            </Button>
            <Button id="buttons" href="contact">
              Contact
            </Button>
          </Toolbar>
        </div>

        <div className="showCase">
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <h1>Universa Localis</h1>
          </Animated>
          <p>The whole universe on your screen</p>
        </div>
        <img
          className="icons"
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/e/e5/NASA_logo.svg/2000px-NASA_logo.svg.png"
          alt="Nasa"
        />

        <div className={classes.body}>
          <Card className={classes.card}>
            <CardActionArea>
              <CardMedia
                component="img"
                alt=""
                className={classes.media}
                height="140"
                image="https://apod.nasa.gov/apod/image/1812/GeminidAurora_Hansen1_960.jpg"
                title="Astronomy Picture"
                onClick={this.handleApod}
              />
              <CardContent>
                <Typography
                  className={classes.text}
                  gutterBottom
                  variant="h5"
                  component="h2"
                >
                  APOD
                </Typography>
                <Typography className={classes.text} component="p">
                  Get the astronomy picture of the day, everyday!
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>

          <Card className={classes.card}>
            <CardActionArea>
              <CardMedia
                component="img"
                alt=""
                className={classes.media}
                height="140"
                image="http://www.digitaltrends.com/wp-content/uploads/2012/08/curiosity-rover-lead.jpg"
                title="Mars Rover Photos"
                onClick={this.handleMrp}
              />
              <CardContent>
                <Typography
                  className={classes.text}
                  gutterBottom
                  variant="h5"
                  component="h2"
                >
                  Mars Rover Photos
                </Typography>
                <Typography className={classes.text} component="p">
                  Get the photos taken by the rovers on Mars!
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>

          <Card className={classes.card}>
            <CardActionArea>
              <CardMedia
                component="img"
                alt=""
                className={classes.media}
                height="140"
                image="https://apod.nasa.gov/apod/image/1812/GeminidAurora_Hansen1_960.jpg"
                title="Astronomy Picture"
                onClick={this.handleApod}
              />
              <CardContent>
                <Typography
                  className={classes.text}
                  gutterBottom
                  variant="h5"
                  component="h2"
                >
                  APOD
                </Typography>
                <Typography className={classes.text} component="p">
                  Get the astronomy picture of the day, everyday!
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        </div>
        <div className={classes.failure}>
          <h2>{this.props.failureContent.title}</h2>
          <h3>{this.props.failureContent.content}</h3>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    failureContent: state.failureContent
  };
};

const withstyles = withStyles(styles)(Main);
const mainConnected = connect(mapStateToProps)(withstyles);
export default mainConnected;
