import {
  SUBMITTED,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  SHOW_APOD,
  SELECT_OPEN,
  SELECT_CLOSE,
  MRP_NAME_UPDATE,
  MRP_DATE_UPDATE,
  IVL_KEY_UPDATE,
  IVL_YEAR_UPDATE,
  SHOW_RESULT
} from "./action_types";

//  APOD  ////////////////////////////////////////////////////

export function getApod() {
  return function(dispatch) {
    fetch("http://localhost:3000/apod")
      .then(response => response.json())
      .then(data => {
        const received = {
          artist: data.copyright,
          title: data.title,
          date: data.date,
          explanation: data.explanation,
          link: data.url
        };
        dispatch(fetchSuccess());
        dispatch(showApod(received));
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

export function showApod(received) {
  return function(dispatch) {
    dispatch({
      type: SHOW_APOD,
      payload: true,
      receivedinfo: received
    });
  };
}

//  MRP  ////////////////////////////////////////////////////

export function onOpen() {
  return function(dispatch) {
    dispatch({
      type: SELECT_OPEN,
      payload: true
    });
  };
}
export function onClose() {
  return function(dispatch) {
    dispatch({
      type: SELECT_CLOSE,
      payload: false
    });
  };
}

export function mrpNameUpdate(name) {
  return function(dispatch) {
    dispatch({
      type: MRP_NAME_UPDATE,
      payload: name
    });
  };
}

export function mrpDateUpdate(date) {
  return function(dispatch) {
    dispatch({
      type: MRP_DATE_UPDATE,
      payload: date
    });
  };
}

export function getMrp() {
  return function(dispatch, getState) {
    const state = getState().FormReducer.mrpForm;
    const sent = {
      name: state.name,
      date: state.date
    };
    const endpoint = `https://api.nasa.gov/mars-photos/api/v1/rovers/${
      sent.name
    }/photos?earth_date=${
      sent.date
    }&api_key=1bLpXwhcARlJOhP0k9rQ0PBxWtO7JJRflDIeg16p`;

    fetch(endpoint)
      .then(response => response.json())
      .then(data => {
        const received = {
          name: data.photos.map(e => e.rover.name),
          earth_date: data.photos.map(e => e.earth_date),
          photos: data.photos
        };

        console.log(received);
        dispatch(fetchSuccess());
        dispatch(showMrp(received));
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

export function showResult(received) {
  return function(dispatch) {
    dispatch({
      type: SHOW_RESULT,
      payload: true,
      receivedinfo: received
    });
  };
}

export function submitted() {
  return function(dispatch) {
    dispatch({
      type: SUBMITTED,
      payload: true
    });
  };
}

export function fetchSuccess() {
  return function(dispatch) {
    dispatch({
      type: FETCH_SUCCESS,
      payload: true
    });
  };
}

export function fetchFailure() {
  return function(dispatch) {
    dispatch({
      type: FETCH_FAILURE,
      payload: true
    });
  };
}
