import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import { withStyles } from "@material-ui/core/styles";

const styles = theme => ({
  button: {
    backgroundColor: "#e040fb"
  },
  container: {
    textAlign: "center"
  },
  header: {
    paddingTop: 100,
    textAlign: "center",
    color: "#d8dcf2"
  },
  image: {
    height: 300,
    width: 500,

    borderRadius: 20
  },
  content: {
    color: "#d8dcf2"
  }
});

class ContactResult extends Component {
  render() {
    const { classes } = this.props;

    let button;
    if (this.props.onReset) {
      button = (
        <Button
          className={classes.button}
          variant="contained"
          color="secondary"
          type="submit"
          onClick={this.props.onReset}
        >
          Go Back!
        </Button>
      );
    }
    return (
      <div className={classes.container}>
        <h1 className={classes.header}>Contact</h1>
        <img className={classes.image} src={this.props.image} alt="dogs" />
        <h3 className={classes.content}>{this.props.title}</h3>
        <p className={classes.content}>{this.props.content}</p>
        {button}
      </div>
    );
  }
}

export default withStyles(styles)(ContactResult);
