import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import MrpList from "./mrpList";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb"
  },
  date: {
    color: "#E040FB"
  }
};

class MrpResult extends Component {
  render() {
    const { classes } = this.props;

    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>

        <div className={classes.all}>
          <div className={classes.body}>
            <h1 className={classes.header}>
              {this.props.receivedInfo.name[0]}
            </h1>
            <h3 className={classes.info}>
              Date: {this.props.receivedInfo.earth_date[0]}
            </h3>
            {this.props.receivedInfo.img_src.map(image => (
              <MrpList img_src={image} />
            ))}

            {this.props.receivedInfo.camera.map(camera => (
              <MrpList camera={camera} />
            ))}
          </div>

          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            style={{ marginBottom: 70 }}
            href="/mrp"
          >
            Go Back
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(MrpResult);
