import React from "react";
import { withStyles } from "@material-ui/core/styles";

const styles = {
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: 50
  },
  title: {
    color: "#E040FB",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "60px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto",
    marginBottom: 40
  },
  text: {
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    color: "#30323e",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    marginBottom: 50,
    position: "relative",
    bottom: 40,
    padding: 30,
    borderRadius: 20,
    width: "90%",
    alignSelf: "center"
  }
};

function IvlList(props) {
  const { classes } = props;

  let title = props.title;
  let shortTitles;
  if (title.length > 20) {
    shortTitles = null;
  } else {
    shortTitles = title;
  }
  return (
    <div className={classes.all}>
      <h1 className={classes.title}>{shortTitles}</h1>
      <div className={classes.grid}>
        <img className={classes.picture} src={props.photo} alt="" />
        <h3 className={classes.text}>{props.description}</h3>
      </div>
    </div>
  );
}

export default withStyles(styles)(IvlList);
