import React from "react";
import Button from "@material-ui/core/Button";

const headerStyle = {
  color: "#ffcc00",
  fontFamily: "Quicksand",
  fontWeight: "bold",
  fontSize: "40px",
  position: "relative",
  bottom: "100px"
};

const gridStyle = {
  display: "grid",
  gridTemplateColumns: "repeat( auto-fill, minmax(420px, 1fr) )",
  gridColumnGap: "10px",
  gridRowGap: "70px",
  position: "relative",
  bottom: "80px"
};

const imgStyle = {
  width: "100%",
  height: "auto"
};

const textStyle = {
  color: "30323e",
  fontFamily: "Quicksand",
  backgroundColor: "#d8dcf2",
  opacity: "0.6",
  fontWeight: "bold"
};

function FormResult(props) {
  return (
    <React.Fragment>
      <h1 style={headerStyle}>{props.receivedInfo.title}</h1>
      <div style={gridStyle}>
        <div style={textStyle}>
          <h3>About the Work</h3>
          <p>
            {props.receivedInfo.explanation}
          </p>
        </div>
        <img
          style={imgStyle}
          src="http://idora.milliyet.com.tr//OnedioGallery/Detail/2017/01/23/2197_pt5_01.jpg"
          alt="Dali"
        />
        <div style={textStyle}>
          <h3>About the Artist</h3>
          <p>
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry. Lorem Ipsum has been the industry's standard dummy text
            ever since the 1500s, when an unknown printer took a galley of type
            and scrambled it to make a type specimen book. It has survived not
            only five centuries, but also the leap into electronic typesetting,
            remaining essentially unchanged. It was popularised in the 1960s
            with the release of Letraset sheets containing Lorem Ipsum passages,
            and more recently with desktop publishing software like Aldus
            PageMaker including versions of Lorem Ipsum.
          </p>
        </div>
      </div>
      <Button
        variant="contained"
        color="secondary"
        style={{ marginBottom: 70 }}
        onClick={() => this.props.onReset()}
      >
        Go Back
      </Button>
    </React.Fragment>
  );
}

export default FormResult;
