import React, { Component } from "react";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import "./main.css";

const styles = theme => ({
  loadingStyle: {
    position: "relative",
    bottom: 120,
    color: "#d8dcf2"
  },
  root: {
    flexGrow: 1
  },
  grow: {
    flexGrow: 1
  }
});

class Main extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <div className={classes.root}>
          <AppBar position="static">
            <Toolbar>
              <IconButton
                className={classes.menuButton}
                color="inherit"
                aria-label="Menu"
              />
              <Typography variant="h6" color="inherit" className={classes.grow}>
                About
              </Typography>
              <Typography variant="h6" color="inherit" className={classes.grow}>
                Contact
              </Typography>
            </Toolbar>
          </AppBar>
        </div>
        <div className="ShowCase">
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <h1>Museum Locale</h1>
          </Animated>
          <p>
            <strong>Your Local Museum</strong>
          </p>
        </div>
        <img
          className="Icons"
          src="https://pbs.twimg.com/profile_images/472484809390845952/al5G_CPq_400x400.jpeg"
          alt="TheMet"
        />
        <img
          className="Icons"
          src="http://blogs.newschool.edu/arts-social-engagement/files/2018/05/met.jpg"
          alt="TheMet"
        />
        <img
          className="Icons"
          src="https://upload.wikimedia.org/wikipedia/commons/f/fc/Artsy_logo.svg"
          alt="TheMet"
        />
        <div className={classes.loadingStyle}>
          <h1>{this.props.title}</h1>
          <h3 style={{}}>{this.props.content}</h3>
          <img src={this.props.image} alt="" />
        </div>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(Main);
