import React from "react";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const headerStyle = {
  color: "#ffcc00",
  fontFamily: "Quicksand",
  fontWeight: "bold",
  fontSize: "40px",
  position: "relative",
  bottom: "100px"
};

const gridStyle = {
  display: "grid",
  gridTemplateColumns: "repeat( auto-fill, minmax(420px, 1fr) )",
  gridColumnGap: "10px",
  gridRowGap: "70px",
  position: "relative",
  bottom: "80px"
};

const textStyle = {
  color: "30323e",
  fontFamily: "Quicksand",
  backgroundColor: "#d8dcf2",
  opacity: "0.6",
  fontWeight: "bold"
};

function FormResult(props) {
  return (
    <React.Fragment>
      <Toolbar>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <Button href="/">Universa Localis</Button>
        </Animated>
      </Toolbar>
      <h1 style={headerStyle}>{props.receivedInfo.title}</h1>
      <h3 style={headerStyle}>{props.receivedInfo.date}</h3>
      <h3 style={headerStyle}>by {props.receivedInfo.artist}</h3>
      <div style={gridStyle}>
        <div style={textStyle}>
          <h3>About the Work</h3>
          <p>{props.receivedInfo.explanation}</p>
        </div>
        <iframe
          title="video"
          width="420"
          height="315"
          src={props.receivedInfo.link}
        >
          {" "}
        </iframe>
      </div>
      <Button
        variant="contained"
        color="secondary"
        style={{ marginBottom: 70 }}
        onClick={() => this.props.onReset()}
      >
        Go Back
      </Button>
    </React.Fragment>
  );
}

export default FormResult;
