import React from "react";
import { withStyles } from "@material-ui/core/styles";

const styles = {
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  body: {
    position: "relative",
    top: 100
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto",
    marginBottom: 40,
    position: "relative",
    bottom: 80
  }
};

function MrpList(props) {
  const { classes } = props;

  return (
    <div className={classes.all}>
      <div className={classes.body}>
        <h1 className={classes.header}>{props.name}</h1>
        <h3 className={classes.info}>Date: {props.earth_date}</h3>
        <h3 className={classes.info}>{props.camera}</h3>
        <div className={classes.grid}>
          <img className={classes.picture} src={props.img_src} alt="" />
        </div>
      </div>
    </div>
  );
}

export default withStyles(styles)(MrpList);
