import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import { getApod } from "../actions/formActions/actions";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import BottomNavigation from "@material-ui/core/BottomNavigation";
import BottomNavigationAction from "@material-ui/core/BottomNavigationAction";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3.5vmax",
    position: "relative",
    bottom: "70px",
    marginBottom: "3rem"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "80px"
  },
  text: {
    color: "#30323e",
    fontFamily: "Quicksand",
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    fontWeight: "bold",
    padding: 30,
    marginTop: 50
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127d3",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "3vmax",
    position: "relative",
    bottom: "100px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  buttonContainer: {
    width: "30rem",
    float: "right"
  },
  buttons: {
    background: "transparent",
    color: "#d8dcf2",
    margin: "0px 10px",
    padding: "1em",
    border: "1px solid white",
    borderRadius: 7
  },
  button: {
    backgroundColor: "#e040fb",
    marginBottom: 160,
    maxWidth: "100%",
    maxHeight: "100vh"
  },
  about: {
    fontFamily: "Righteous"
  },
  toolbar: {
    display: "flex"
  }
};

class ApodResult extends Component {
  componentDidMount() {
    this.props.dispatch(getApod());
  }
  render() {
    const { classes } = this.props;

    let renderedMedia;

    if (this.props.receivedInfo.media_type === "video") {
      renderedMedia = (
        <iframe
          title="video"
          className={classes.picture}
          src={this.props.receivedInfo.link}
          allowfullscreen="true"
          width="640"
          height="480"
        />
      );
    } else {
      renderedMedia = (
        <img
          className={classes.picture}
          src={this.props.receivedInfo.link}
          alt=""
        />
      );
    }

    return (
      <React.Fragment>
        <div className="toolbar">
          <Toolbar>
            <Animated
              animationIn="fadeInRight"
              animationOut="fadeOut"
              isVisible={true}
            >
              <Button className={classes.home} href="/">
                Universa Localis
              </Button>
            </Animated>
            <div className={classes.buttonContainer}>
              <Button className={classes.buttons} href="/">
                Home
              </Button>
              <Button className={classes.buttons} href="about">
                About
              </Button>
              <Button className={classes.buttons} href="contact">
                Contact
              </Button>
            </div>
          </Toolbar>
        </div>

        <div className={classes.all}>
          <div className={classes.body}>
            <div>
              <h1 className={classes.header}>
                {this.props.receivedInfo.title}
              </h1>
              <h3 className={classes.info}>
                Date: {this.props.receivedInfo.date}
              </h3>
              <h3 className={classes.info}>{this.props.receivedInfo.artist}</h3>
            </div>
            <div className={classes.grid}>
              {renderedMedia}
              <div className={classes.text}>
                <h3 className={classes.about}>About the Work</h3>
                <p>{this.props.receivedInfo.explanation}</p>
              </div>
            </div>
            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              href="/"
            >
              Go Back
            </Button>
          </div>
        </div>
        <BottomNavigation showLabels id="footer">
          <BottomNavigationAction id="link" label="Home" href="/" />
          <BottomNavigationAction id="link" label="About" href="about" />
          <BottomNavigationAction id="link" label="Contact" href="contact" />
          <p id="copyright">© copyright Mehmet Tansu Işıklı</p>
        </BottomNavigation>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    receivedInfo: state.receivedInfo
  };
};

ApodResult.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(ApodResult);
const apodResultConnected = connect(mapStateToProps)(withstyles);
export default apodResultConnected;
