import {
  LOADING,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  SHOW_APOD,
  SHOW_MRP
} from "./action_types";

export function getApod() {
  return function(dispatch) {
    fetch("http://localhost:3000/apod")
      .then(response => response.json())
      .then(data => {
        const received = {
          artist: data.copyright,
          title: data.title,
          date: data.date,
          explanation: data.explanation,
          link: data.url
        };
        dispatch(showApod(received));
      });
  };
}

export function getMrp() {
  return function(dispatch) {
    fetch("http://localhost:3000/mrp")
      .then(response => response.json())
      .then(data => {
        const received = {
          image: data.photos[0].img_src
        };
        dispatch(showMrp(received));
      });
  };
}

export function showApod(received) {
  return function(dispatch) {
    dispatch({
      type: SHOW_APOD,
      payload: true,
      receivedObject: received
    });
  };
}

export function showMrp(received) {
  return function(dispatch) {
    dispatch({
      type: SHOW_MRP,
      payload: true,
      receivedObject: received
    });
  };
}

export function loading() {
  return function(dispatch) {
    dispatch({
      type: LOADING,
      payload: true
    });
  };
}

export function fetchSuccess() {
  return function(dispatch) {
    dispatch({
      type: FETCH_SUCCESS,
      payload: true
    });
  };
}

export function fetchFailure() {
  return function(dispatch) {
    dispatch({
      type: FETCH_FAILURE,
      payload: true
    });
  };
}
