import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb"
  },
  date: {
    color: "#E040FB"
  }
};

function MrpResult(props) {
  const { classes } = props;

  let nameShow = props.receivedInfo.name.map(name => (
    <h1 className={classes.header}>{name}</h1>
  ));
  let dateShow = props.receivedInfo.earth_date.map(date => (
    <h3 className={classes.info}>Date: {date}</h3>
  ));
  let cameraShow = props.receivedInfo.camera.map(camera => (
    <h3 className={classes.info}>Camera: {camera}</h3>
  ));
  let imgShow = props.receivedInfo.img_src.map(image => (
    <img className={classes.picture} src={image} alt="" />
  ));

  /*
  const rendered = () => {
    for (let i = 0; i < names.length; i++) {
      nameShow = names[i];
      return snippet;
    }

    for (let i = 0; i < earth_dates.length; i++) {
      dateShow = earth_dates[i];
      return snippet;
    }

    for (let i = 0; i < cameras.length; i++) {
      cameraShow = cameras[i];
      return snippet;
    }

    for (let i = 0; i < img_srcs.length; i++) {
      imgShow = img_srcs[i];
      return snippet;
    }
  };
  */

  const rendered = (
    <div className={classes.body}>
      <div>
        <h1 className={classes.header}>{nameShow}</h1>
        <h3 className={classes.info}>Date: {dateShow}</h3>
        <h3 className={classes.info}>Camera: {cameraShow}</h3>
      </div>
      <div className={classes.grid}>
        <img className={classes.picture} src={imgShow} alt="" />
      </div>
    </div>
  );

  return (
    <React.Fragment>
      <Toolbar>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <Button className={classes.home} href="/">
            Universa Localis
          </Button>
        </Animated>
      </Toolbar>
      <div className={classes.all}>
        {rendered}
        <Button
          className={classes.button}
          variant="contained"
          color="primary"
          style={{ marginBottom: 70 }}
          href="/mrp"
        >
          Go Back
        </Button>
      </div>
    </React.Fragment>
  );
}

export default withStyles(styles)(MrpResult);
