import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import { ivlKeyUpdate, ivlYearUpdate } from "../../actions/formActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { getIvl } from "../../actions/formActions/actions";
import BottomNavigation from "@material-ui/core/BottomNavigation";
import BottomNavigationAction from "@material-ui/core/BottomNavigationAction";

const styles = theme => ({
  all: {
    display: "grid",
    justifyItems: "center"
  },
  root: {
    backgroundColor: "rgba(200, 200, 200, .8)",
    minWidth: 250,
    maxWidth: 400,
    borderRadius: 20,
    padding: 30
  },
  formControl: {
    margin: theme.spacing.unit,
    width: 300,
    minWidth: 100
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  header: {
    fontFamily: "Righteous",
    color: "#E040FB",
    height: 80,
    fontSize: "3rem",
    marginTop: 50,
    marginBottom: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  button: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  },
  footer: {
    position: "absolute",
    bottom: 0
  }
});

class IvlForm extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <div className={classes.all}>
          <h1 className={classes.header}>Image and Video Library</h1>
          <p className={classes.helper}>Please enter a keyword and a year</p>
          <div className={classes.root}>
            <FormControl className={classes.formControl}>
              <TextField
                label="Search"
                placeholder="ex. Earth"
                value={this.props.keyword}
                onChange={event => {
                  let value = event.target.value;
                  this.props.dispatch(ivlKeyUpdate(value));
                }}
              />
            </FormControl>

            <FormControl className={classes.formControl}>
              <TextField
                label="Select Year"
                placeholder="ex. 2018"
                value={this.props.year}
                onChange={event => {
                  let value = event.target.value;
                  this.props.dispatch(ivlYearUpdate(value));
                }}
              />
            </FormControl>
            <Button
              variant="contained"
              color="primary"
              className={classes.button}
              type="submit"
              onClick={() => {
                this.props.dispatch(getIvl());
              }}
            >
              Submit
            </Button>
          </div>
        </div>
        <BottomNavigation showLabels id="footer" className={classes.footer}>
          <BottomNavigationAction id="link" label="Home" href="/" />
          <BottomNavigationAction id="link" label="About" href="about" />
          <BottomNavigationAction id="link" label="Contact" href="contact" />
          <p id="copyright">© copyright Mehmet Tansu Işıklı</p>
        </BottomNavigation>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    keyword: state.ivlForm.keyword,
    year: state.ivlForm.year
  };
};

IvlForm.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(IvlForm);
const mrpFormConnected = connect(mapStateToProps)(withstyles);
export default mrpFormConnected;
