import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { starNameUpdate, getFinalQuery } from "../actions/formActions/actions";
import { withStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "5vmax",
    position: "relative",
    bottom: "70px",
    marginBottom: "5rem"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "80px"
  },
  about: {
    fontFamily: "Righteous"
  },
  text: {
    color: "#30323e",
    fontFamily: "Quicksand",
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    fontWeight: "bold",
    padding: 30
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127d3",
    borderRadius: "5%",
    margin: 30
  },
  formControl: {
    alignSelf: "center",
    width: "30vmax",
    backgroundColor: "#d8dcf2ce",
    padding: 10,
    borderRadius: 5
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "3vmax",
    position: "relative",
    bottom: "100px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    backgroundColor: "#e040fb",
    maxWidth: "100%",
    height: "auto"
  },
  query: {
    display: "flex",
    justifyContent: "center",
    marginBottom: 50
  }
};

class StarmapResult extends Component {
  render() {
    const { classes } = this.props;

    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <div className={classes.all}>
          <div className={classes.body}>
            <div>
              <h1 className={classes.header}>The Star Map</h1>
            </div>
            <div className={classes.grid}>
              <div className={classes.query}>
                <FormControl className={classes.formControl}>
                  <TextField
                    label="Search"
                    placeholder="ex. Sirius"
                    value={this.props.starName}
                    onChange={event => {
                      let value = event.target.value;
                      this.props.dispatch(starNameUpdate(value));
                    }}
                  />
                </FormControl>
                <Button
                  className={classes.button}
                  variant="contained"
                  color="primary"
                  type="submit"
                  href="starmap"
                  onClick={() => {
                    this.props.dispatch(getFinalQuery());
                  }}
                >
                  Go!
                </Button>
              </div>
              <iframe
                className={classes.picture}
                title="skymap"
                src={this.props.finalQuery}
                width="800"
                height="640"
              />
            </div>
            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              style={{ marginBottom: 70 }}
              href="/"
            >
              Go Back
            </Button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    starName: state.starForm.starName,
    finalQuery: state.starForm.finalQuery
  };
};

StarmapResult.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(StarmapResult);
const mrpFormConnected = connect(mapStateToProps)(withstyles);
export default mrpFormConnected;
