import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import IvlList from "./ivlList";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 70
  },
  all: {
    backgroundColor: "#212127",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    backgroundColor: "#e040fb",
    position: "relative",
    bottom: 40
  },
  date: {
    color: "#E040FB"
  }
};

class ivlResult extends Component {
  render() {
    const { classes } = this.props;

    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>

        <div className={classes.all}>
          <div className={classes.body}>
            {this.props.receivedInfo.items.map(i => (
              <IvlList
                title={i.data.title}
                description={i.data.description}
                image={i.links.href}
              />
            ))}

            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              style={{ marginBottom: 70 }}
              href="/mrp"
            >
              Go Back
            </Button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(ivlResult);
