import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import { connect } from "react-redux";
import { fetchStart } from "../actions/formActions/actions";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import "../components/main.css";

import {
  onClose,
  onOpen,
  artistUpdate,
  movementUpdate,
  centuryUpdate,
  branchUpdate
} from "../actions/formActions/actions";

const styles = theme => ({
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
    width: 250,
    WebkitTextFillColor: "#d8dcf2",
    WebkitBorderBeforeColor: "#d8dcf2"
  },

  container: {
    display: "grid",
    gridTemplateColumns: "auto",
    justifyContent: "center",
    position: "relative",
    bottom: "120px"
  },

  button: {
    position: "relative",
    top: 30
  }
});

class SelectForm extends React.Component {
  render() {
    const { classes } = this.props;
    return (
      <div>
        <form
          className={classes.container}
          onSubmit={() => this.props.dispatch(fetchStart())}
        >
          <FormControl className={classes.formControl}>
            <InputLabel>Artist</InputLabel>
            <Select
              value={this.props.artist}
              open={this.props.open}
              onClose={() => {
                this.props.dispatch(onClose());
              }}
              onOpen={() => {
                this.props.dispatch(onOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(artistUpdate(value));
              }}
            >
              <MenuItem value="Alican">Alican</MenuItem>
              <MenuItem value="Tansu">Tansu</MenuItem>
              <MenuItem value="Tolga">Tolga</MenuItem>
            </Select>
          </FormControl>

          <FormControl className={classes.formControl}>
            <InputLabel>Movement</InputLabel>
            <Select
              value={this.props.movement}
              open={this.props.open}
              onClose={() => {
                this.props.dispatch(onClose());
              }}
              onOpen={() => {
                this.props.dispatch(onOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(movementUpdate(value));
              }}
            >
              <MenuItem value="Surrealism">Surrealism</MenuItem>
              <MenuItem value="Minimalism">Minimalism</MenuItem>
              <MenuItem value="Impressionism">Impressionism</MenuItem>
            </Select>
          </FormControl>

          <FormControl className={classes.formControl}>
            <InputLabel>Century</InputLabel>
            <Select
              value={this.props.century}
              open={this.props.open}
              onClose={() => {
                this.props.dispatch(onClose());
              }}
              onOpen={() => {
                this.props.dispatch(onOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(centuryUpdate(value));
              }}
            >
              <MenuItem value="Nineteenth Century">Nineteenth Century</MenuItem>
              <MenuItem value="Twentieth Century">Twentieth Century</MenuItem>
              <MenuItem value="Twenty-first Century">
                Twenty-first Century
              </MenuItem>
            </Select>
          </FormControl>

          <FormControl className={classes.formControl}>
            <InputLabel>Branch</InputLabel>
            <Select
              value={this.props.branch}
              open={this.props.open}
              onClose={() => {
                this.props.dispatch(onClose());
              }}
              onOpen={() => {
                this.props.dispatch(onOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(branchUpdate(value));
              }}
            >
              <MenuItem value="Painting">Painting</MenuItem>
              <MenuItem value="Sculpture">Sculpture</MenuItem>
              <MenuItem value="Photography">Photography</MenuItem>
            </Select>
          </FormControl>
          <Button
            variant="contained"
            color="secondary"
            className={classes.button}
            type="submit"
          >
            Submit
          </Button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    artist: state.form.artist,
    movement: state.form.movement,
    century: state.form.century,
    branch: state.form.branch,
    dialog_open: state.dialog_open
  };
};

SelectForm.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(SelectForm);
const selectFormConnected = connect(mapStateToProps)(withstyles);
export default selectFormConnected;
