import React from "react";
import { withStyles } from "@material-ui/core/styles";

const styles = {
  all: {
    backgroundColor: "#212127d3",
    borderRadius: 30,
    margin: "0.1em"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: 50
  },
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "8vmax"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto",
    marginBottom: 40
  },
  text: {
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    color: "#30323e",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "1em",
    marginBottom: 50,
    position: "relative",
    bottom: 40,
    padding: "1em",
    borderRadius: 20,
    width: "90%",
    alignSelf: "center"
  }
};

function IvlList(props) {
  const { classes } = props;
  return (
    <div className={classes.all}>
      <h1 className={classes.header}>{props.title}</h1>
      <div className={classes.grid}>
        <img className={classes.picture} src={props.photo} alt="" />
        <h3 className={classes.text}>{props.description}</h3>
      </div>
    </div>
  );
}

export default withStyles(styles)(IvlList);
