import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import { mrpNameUpdate, mrpDateUpdate } from "../actions/formActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Button from "@material-ui/core/Button";
import {
  onMrpOpen,
  onMrpClose,
  handleMrpSubmit
} from "../actions/formActions/actions";

const styles = theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    flexWrap: "wrap",
    alignItems: "center",
    backgroundColor: "rgba(200, 200, 200, .8)",
    marginLeft: 400,
    marginRight: 400,
    borderRadius: "10%",
    padding: 30
  },
  formControl: {
    margin: theme.spacing.unit,
    width: 300,
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing.unit * 2
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  header: {
    fontFamily: "Righteous",
    color: "#E040FB",
    height: 80,
    fontSize: "3rem",
    marginTop: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  form: {
    width: 300,
    marginBottom: 20
  },
  button: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  }
});

class MrpForm extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <h1 className={classes.header}>Mars Rover Photos</h1>
        <p className={classes.helper}>Please select a rover and a date</p>

        <div className={classes.root}>
          <FormControl className={classes.formControl}>
            <InputLabel>Select Rover</InputLabel>
            <Select
              value={this.props.name}
              onClose={() => {
                this.props.dispatch(onMrpClose());
              }}
              onOpen={() => {
                this.props.dispatch(onMrpOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(mrpNameUpdate(value));
              }}
            >
              <MenuItem value="Rover 1">Curiosity</MenuItem>
              <MenuItem value="Rover 2">Opportunity</MenuItem>
              <MenuItem value="Rover 3">Spirit</MenuItem>
            </Select>
          </FormControl>

          <FormControl className={classes.formControl}>
            <InputLabel>Select Date</InputLabel>
            <Select
              value={this.props.date}
              onClose={() => {
                this.props.dispatch(onMrpClose());
              }}
              onOpen={() => {
                this.props.dispatch(onMrpOpen());
              }}
              onChange={event => {
                let value = event.target.value;
                this.props.dispatch(mrpDateUpdate(value));
              }}
            >
              <MenuItem value="Date 1">Date 1</MenuItem>
              <MenuItem value="Date 2">Date 2</MenuItem>
              <MenuItem value="Date 3">Date 3</MenuItem>
            </Select>
          </FormControl>

          <Button
            variant="contained"
            color="primary"
            className={classes.button}
            type="submit"
            onClick={() => {
              this.props.dispatch(getMrp());
            }}
          >
            Submit
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    name: state.mrpForm.name,
    date: state.mrpForm.date,
    dialog_open: state.dialog_open
  };
};

MrpForm.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(MrpForm);
const mrpFormConnected = connect(mapStateToProps)(withstyles);
export default mrpFormConnected;

