import React, { Component } from "react";
import { connect } from "react-redux";
import Contact from "./contact";
import ContactResult from "../components/contactResult";
import { reset } from "../actions/actions";

class ContactStatus extends Component {
  render() {
    let renderedPage;

    if (!this.props.submitted) {
      renderedPage = <Contact />;
    }

    if (this.props.loading) {
      renderedPage = (
        <ContactResult
          title={this.props.loadingContent.title}
          content={this.props.loadingContent.content}
          image="http://puppytoob.com/wp-content/uploads/2014/11/13912989694_24c387c6b7_z.jpg"
        />
      );
    }

    if (this.props.success) {
      renderedPage = (
        <ContactResult
          title={this.props.successContent.title}
          content={this.props.successContent.content}
          image="https://www.stopthatdog.com/wp-content/uploads/2014/01/excited-dog.jpg"
          onReset={() => {
            this.props.dispatch(reset());
          }}
        />
      );
    }

    if (this.props.failure) {
      renderedPage = (
        <ContactResult
          title={this.props.failureContent.title}
          content={this.props.failureContent.content}
          image="https://www.petmd.com/sites/default/files/Acute-Dog-Diarrhea-47066074.jpg"
          onReset={() => {
            this.props.dispatch(reset());
          }}
        />
      );
    }

    return <div>{renderedPage}</div>;
  }
}

const mapStateToProps = state => {
  return {
    submitted: state.isSubmitted,
    loading: state.isLoading,
    success: state.isSuccess,
    failure: state.isFailure,
    loadingContent: state.loadingContent,
    successContent: state.successContent,
    failureContent: state.failureContent,
    reset: state.reset
  };
};

const connectedTwo = connect(mapStateToProps)(ContactStatus);
export default connectedTwo;
