import {
  SEL_OPEN,
  SEL_CLO,
  ARTIST_UPDATE,
  MOVEMENT_UPDATE,
  CENTURY_UPDATE,
  BRANCH_UPDATE,
  LOADING,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  RESET
} from "./action_types";

export function onOpen() {
  return function(dispatch) {
    dispatch({
      type: SEL_OPEN,
      payload: true
    });
  };
}
export function onClose() {
  return function(dispatch) {
    dispatch({
      type: SEL_CLO,
      payload: false
    });
  };
}

export function artistUpdate(artist) {
  return function(dispatch) {
    dispatch({
      type: ARTIST_UPDATE,
      payload: artist
    });
  };
}

export function movementUpdate(movement) {
  return function(dispatch) {
    dispatch({
      type: MOVEMENT_UPDATE,
      payload: movement
    });
  };
}

export function centuryUpdate(century) {
  return function(dispatch) {
    dispatch({
      type: CENTURY_UPDATE,
      payload: century
    });
  };
}

export function branchUpdate(branch) {
  return function(dispatch) {
    dispatch({
      type: BRANCH_UPDATE,
      payload: branch
    });
  };
}

/////////////////////////// YUKARISI OK ////////////////

export function fetchStart() {
  return function(dispatch, getState) {
    const data = {
      artist: getState().form.artist,
      movement: getState().form.movement,
      century: getState().form.century,
      branch: getState().form.branch
    };
    dispatch(loading());
    fetch("http://localhost:3000/categories", {
      method: "POST",
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(res => {
        if (res.ok) {
          dispatch(fetchSuccess());
          console.log(res.body);
        } else {
          dispatch(fetchFailure());
          console.log("Failure!");
        }
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

export function loading() {
  return function(dispatch) {
    dispatch({
      type: LOADING,
      payload: true
    });
  };
}

export function fetchSuccess() {
  return function(dispatch) {
    dispatch({
      type: FETCH_SUCCESS,
      payload: true
    });
  };
}

export function fetchFailure() {
  return function(dispatch) {
    dispatch({
      type: FETCH_FAILURE,
      payload: true
    });
  };
}

export function reset() {
  return function(dispatch) {
    dispatch({
      type: RESET,
      payload: ""
    });
  };
}
