import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#d8dcf2",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    textDecoration: "underline",
    textDecorationColor: "blue",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "80px",
    margin: "1rem"
  },
  text: {
    color: "#30323e",
    fontFamily: "Quicksand",
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    fontWeight: "bold",
    padding: 30,
    marginTop: 50
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#923600"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "100px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 30
  }
};

function FormResult(props) {
  const { classes } = props;

  return (
    <React.Fragment>
      <Toolbar>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <Button className={classes.home} href="/">
            Universa Localis
          </Button>
        </Animated>
      </Toolbar>
      <div className={classes.all}>
        <div className={classes.body}>
          <div>
            <h1 className={classes.header}>{props.receivedInfo.title}</h1>
            <h3 className={classes.info}>Date: {props.receivedInfo.date}</h3>
            <h3 className={classes.info}>{props.receivedInfo.artist}</h3>
          </div>
          <div className={classes.grid}>
            <img
              className={classes.picture}
              src={props.receivedInfo.link}
              alt=""
            />
            <div className={classes.text}>
              <h3>About the Work</h3>
              <p>{props.receivedInfo.explanation}</p>
            </div>
          </div>
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            style={{ marginBottom: 70 }}
            href="/"
          >
            Go Back
          </Button>
        </div>
      </div>
    </React.Fragment>
  );
}

export default withStyles(styles)(FormResult);
