import React, { Component } from "react";
import ApodResult from "./components/apodResult";
import { connect } from "react-redux";
import Main from "./components/main";

export class App extends Component {
  render() {
    let pageRendered;

    if (!this.props.submitted) {
      pageRendered = <Main />;
    }

    if (this.props.showResults) {
      pageRendered = <ApodResult receivedInfo={this.props.receivedInfo} />;
    }

    if (this.props.failure) {
      pageRendered = (
        <Main
          title={this.props.failureContent.title}
          content={this.props.failureContent.content}
        />
      );
    }

    return <div>{pageRendered}</div>;
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    submitted: state.isSubmitted,
    showResults: state.showResults,
    loading: state.isLoading,
    loadingContent: state.loadingContent,
    failure: state.isFailure,
    failureContent: state.failureContent,
    receivedInfo: state.receivedInfo
  };
};

const AppConnected = connect(mapStateToProps)(App);
export default AppConnected;
