import React, { Component } from "react";
import SpacexList from "./spacexList";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import { connect } from "react-redux";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3.5vmax",
    position: "relative",
    bottom: "70px",
    marginBottom: "5rem"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "80px"
  },
  text: {
    color: "#30323e",
    fontFamily: "Quicksand",
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    fontWeight: "bold",
    padding: 30,
    marginTop: 50
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127d3",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "3vmax",
    position: "relative",
    bottom: "100px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    marginBottom: 70,
    margin: 50,
    width: 300,
    height: 50
  },
  about: {
    fontFamily: "Righteous"
  }
};

class SpacexResult extends Component {
  render() {
    const { classes } = this.props;

    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button href="/">Universa Localis</Button>
          </Animated>
        </Toolbar>

        <div>
          <div />
          {/*  {this.props.receivedInfo.flight_number.map(i => (
            <SpacexList flight_number={this.props.flight_number} />
          ))}
        */}
          <p>{this.props.receivedInfo.flight_number}</p>
          <Button variant="contained" color="primary" href="/">
            Go Back
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    receivedInfo: state.receivedInfo
  };
};

const spacexStatusConnected = connect(mapStateToProps)(SpacexResult);
export default spacexStatusConnected;
