import {
  SUBMITTED,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  SHOW_APOD,
  MRP_NAME_UPDATE,
  MRP_DATE_UPDATE,
  SHOW_MRP
} from "../actions/formActions/action_types";

const initialState = {
  isSubmitted: false,
  isSuccess: false,
  isFailure: false,
  showApod: false,
  mrpForm: {
    name: "",
    date: ""
  },
  showMrp: false,
  receivedInfo: {}
};

function FormReducer(state = initialState, action) {
  switch (action.type) {
    case SUBMITTED:
      return Object.assign({}, state, {
        isSubmitted: action.payload
      });
    case FETCH_SUCCESS:
      return Object.assign({}, state, {
        isSuccess: action.payload
      });
    case FETCH_FAILURE:
      return Object.assign({}, state, {
        isFailure: action.payload
      });
    case SHOW_APOD:
      return Object.assign({}, state, {
        showApod: action.payload,
        receivedInfo: action.receivedObject
      });
    case MRP_NAME_UPDATE: {
      const newState = Object.assign({}, state);
      newState.mrpForm.name = action.payload;
      return newState;
    }
    case MRP_DATE_UPDATE: {
      const newState = Object.assign({}, state);
      newState.mrpForm.date = action.payload;
      return newState;
    }
    case SHOW_MRP:
      return Object.assign({}, state, {
        showMrp: action.payload,
        receivedInfo: action.receivedObject
      });
    default:
      return state;
  }
}

export default FormReducer;
