import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import { withStyles } from "@material-ui/core/styles";
import Toolbar from "@material-ui/core/Toolbar";
import { Animated } from "react-animated-css";
import BottomNavigation from "@material-ui/core/BottomNavigation";

const styles = theme => ({
  button1: {
    backgroundColor: "#e040fb"
  },
  all: {
    textAlign: "center",
    position: "relative",
    bottom: 100
  },
  header: {
    paddingTop: 100,
    textAlign: "center",
    color: "#d8dcf2"
  },
  image: {
    height: "30vmax",
    width: "50vmax",
    borderRadius: 20
  },
  content: {
    color: "#d8dcf2"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb",
    float: "left"
  },
  buttonContainer: {
    width: "95%",
    display: "block"
  },
  button: {
    marginTop: 20,
    backgroundColor: "#e040fb"
  }
});

class ContactResult extends Component {
  render() {
    const { classes } = this.props;

    let button;
    if (this.props.onReset) {
      button = (
        <Button
          className={classes.button1}
          variant="contained"
          color="secondary"
          type="submit"
          onClick={this.props.onReset}
        >
          Go Back!
        </Button>
      );
    }
    return (
      <React.Fragment>
        <div id="container">
          <Toolbar className={classes.buttonContainer}>
            <Animated
              animationIn="fadeInRight"
              animationOut="fadeOut"
              isVisible={true}
            >
              <Button id="home" className={classes.home} href="/">
                Universa Localis
              </Button>
            </Animated>
            <div id="bb">
              <Button id="buttons" className={classes.buttons} href="/">
                Home
              </Button>
              <Button id="buttons" className={classes.buttons} href="about">
                About
              </Button>
              <Button id="buttons" className={classes.buttons} href="contact">
                Contact
              </Button>
            </div>
          </Toolbar>
          <div className={classes.all}>
            <h1 className={classes.header}>Contact</h1>
            <img className={classes.image} src={this.props.image} alt="dogs" />
            <h3 className={classes.content}>{this.props.title}</h3>
            <p className={classes.content}>{this.props.content}</p>
            {button}
          </div>
        </div>
        <BottomNavigation showLabels id="footer">
          <p id="copyright">© copyright Mehmet Tansu Işıklı</p>
        </BottomNavigation>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(ContactResult);
