import {
  NAME_UPDATE,
  EMAIL_UPDATE,
  MESSAGE_UPDATE,
  SUBMITTED,
  LOADING,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  RESET
} from "../actions/contactActions/action_types";

const initialState = {
  form: {
    name: "",
    email: "",
    message: ""
  },
  isSubmitted: false,
  isLoading: false,
  loadingContent: {
    title: "Loading...",
    content: "Please Wait!"
  },
  isSuccess: false,
  successContent: {
    title: "Thank You!",
    content: "Your message has been sent!"
  },
  isFailure: false,
  failureContent: {
    title: "There has been a problem!",
    content: "Please try again"
  },
  reset: false
};

export function contactReducer(state = initialState, action) {
  switch (action.type) {
    case NAME_UPDATE: {
      const newState = Object.assign({}, state);
      newState.form.name = action.payload;
      return newState;
    }
    case EMAIL_UPDATE: {
      const newState = Object.assign({}, state);
      newState.form.email = action.payload;
      return newState;
    }
    case MESSAGE_UPDATE: {
      const newState = Object.assign({}, state);
      newState.form.message = action.payload;
      return newState;
    }
    case SUBMITTED:
      return Object.assign({}, state, {
        submitted: action.payload
      });
    case LOADING:
      return Object.assign({}, state, {
        isLoading: action.payload
      });
    case FETCH_SUCCESS:
      return Object.assign({}, state, {
        isSuccess: action.payload
      });
    case FETCH_FAILURE:
      return Object.assign({}, state, {
        isFailure: action.payload
      });
    case RESET: {
      const newState = Object.assign({}, (state = initialState));
      newState.form.name = action.payload;
      newState.form.email = action.payload;
      newState.form.message = action.payload;
      return JSON.parse(JSON.stringify(newState));
    }
    default:
      return state;
  }
}
