import React from "react";
import { withStyles } from "@material-ui/core/styles";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb"
  },
  date: {
    color: "#E040FB"
  }
};

function MrpList(props) {
  const { classes } = props;

  return (
    <div className={classes.body}>
      <div>
        <h1 className={classes.header}>{props.name}</h1>
        <h3 className={classes.info}>Date: {props.earth_date}</h3>
        <h3 className={classes.info}>Camera: {props.camera}</h3>
      </div>
      <div className={classes.grid}>
        <img className={classes.picture} src={props.img_src} alt="" />
      </div>
    </div>
  );
}

export default withStyles(styles)(MrpList);
