import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb"
  },
  date: {
    color: "#E040FB"
  }
};

function MrpResult(props) {
  const { classes } = props;

  const names = [];
  const earth_dates = [];
  const cameras = [];
  const img_srcs = [];

  names.push(props.receivedInfo.name);
  earth_dates.push(props.receivedInfo.earth_date);
  cameras.push(props.receivedInfo.camera);
  img_srcs.push(props.receivedInfo.img_src);

  let nameShow;
  let dateShow;
  let cameraShow;
  let imgShow;

  for (let i = 0; i < names.length; i++) {
    nameShow = names[i];
    return (
      <div className={classes.all}>
        <div className={classes.body}>
          <div>
            <h1 className={classes.header}>{nameShow}</h1>
            <h3 className={classes.info}>Date: {dateShow}</h3>
            <h3 className={classes.info}>Camera: {cameraShow}</h3>
          </div>
          <div className={classes.grid}>
            <img className={classes.picture} src={imgShow} alt="" />
          </div>
        </div>
      </div>
    );
  }

  for (let i = 0; i < earth_dates.length; i++) {
    dateShow = earth_dates[i];
    return (
      <div className={classes.all}>
        <div className={classes.body}>
          <div>
            <h1 className={classes.header}>{nameShow}</h1>
            <h3 className={classes.info}>Date: {dateShow}</h3>
            <h3 className={classes.info}>Camera: {cameraShow}</h3>
          </div>
          <div className={classes.grid}>
            <img className={classes.picture} src={imgShow} alt="" />
          </div>
        </div>
      </div>
    );
  }

  for (let i = 0; i < cameras.length; i++) {
    cameraShow = cameras[i];
    return (
      <div className={classes.all}>
        <div className={classes.body}>
          <div>
            <h1 className={classes.header}>{nameShow}</h1>
            <h3 className={classes.info}>Date: {dateShow}</h3>
            <h3 className={classes.info}>Camera: {cameraShow}</h3>
          </div>
          <div className={classes.grid}>
            <img className={classes.picture} src={imgShow} alt="" />
          </div>
        </div>
      </div>
    );
  }

  for (let i = 0; i < img_srcs.length; i++) {
    imgShow = img_srcs[i];
    return (
      <div className={classes.all}>
        <div className={classes.body}>
          <div>
            <h1 className={classes.header}>{nameShow}</h1>
            <h3 className={classes.info}>Date: {dateShow}</h3>
            <h3 className={classes.info}>Camera: {cameraShow}</h3>
          </div>
          <div className={classes.grid}>
            <img className={classes.picture} src={imgShow} alt="" />
          </div>
        </div>
      </div>
    );
  }

  const rendered = (
    <div className={classes.all}>
      <div className={classes.body}>
        <div>
          <h1 className={classes.header}>{nameShow}</h1>
          <h3 className={classes.info}>Date: {dateShow}</h3>
          <h3 className={classes.info}>Camera: {cameraShow}</h3>
        </div>
        <div className={classes.grid}>
          <img className={classes.picture} src={imgShow} alt="" />
        </div>
      </div>
    </div>
  );

  return (
    <React.Fragment>
      <Toolbar>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <Button className={classes.home} href="/">
            Universa Localis
          </Button>
        </Animated>
      </Toolbar>
      {rendered}
      <Button
        className={classes.button}
        variant="contained"
        color="primary"
        style={{ marginBottom: 70 }}
        href="/mrp"
      >
        Go Back
      </Button>
    </React.Fragment>
  );
}

export default withStyles(styles)(MrpResult);
